package Assaignment.Izeal;

public class PrintOddandEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int max = 100;
		
		for(int i=1;i<=max;i++){
			if (i%2!=0){
				System.out.println(i);
			}
			else{
				System.out.println("*");
			}
			
			
		}

	}

}
